mapper.select:

select 

,

from nk_doc_def_i
where CARD_KEY = #{cardKey,jdbcType=VARCHAR}
  and DOC_TYPE = #{docType,jdbcType=VARCHAR}
  and VERSION = #{version,jdbcType=VARCHAR}

mapper.delete:

delete from nk_doc_def_i
where CARD_KEY = #{cardKey,jdbcType=VARCHAR}
  and DOC_TYPE = #{docType,jdbcType=VARCHAR}
  and VERSION = #{version,jdbcType=VARCHAR}
