mapper.select:

select 

,

from nk_doc_i_bill
where BILL_PARTNER_ID = #{billPartnerId,jdbcType=VARCHAR}
  and BILL_TYPE = #{billType,jdbcType=VARCHAR}
  and CARD_KEY = #{cardKey,jdbcType=VARCHAR}
  and DOC_ID = #{docId,jdbcType=VARCHAR}
  and EXPIRE_DATE = #{expireDate,jdbcType=BIGINT}

mapper.delete:

delete from nk_doc_i_bill
where BILL_PARTNER_ID = #{billPartnerId,jdbcType=VARCHAR}
  and BILL_TYPE = #{billType,jdbcType=VARCHAR}
  and CARD_KEY = #{cardKey,jdbcType=VARCHAR}
  and DOC_ID = #{docId,jdbcType=VARCHAR}
  and EXPIRE_DATE = #{expireDate,jdbcType=BIGINT}

mapper.insert:

insert into nk_doc_i_bill (BILL_PARTNER_ID, BILL_TYPE, CARD_KEY, 
  DOC_ID, EXPIRE_DATE, AMOUNT, 
  RECEIVED, RECEIVABLE, STATE, 
  DISCARD, UPDATED_TIME, DETAILS
  )
values (#{billPartnerId,jdbcType=VARCHAR}, #{billType,jdbcType=VARCHAR}, #{cardKey,jdbcType=VARCHAR}, 
  #{docId,jdbcType=VARCHAR}, #{expireDate,jdbcType=BIGINT}, #{amount,jdbcType=DOUBLE}, 
  #{received,jdbcType=DOUBLE}, #{receivable,jdbcType=DOUBLE}, #{state,jdbcType=INTEGER}, 
  #{discard,jdbcType=INTEGER}, #{updatedTime,jdbcType=BIGINT}, #{details,jdbcType=LONGVARCHAR}
  )

mapper.update:

update nk_doc_i_bill


    AMOUNT = #{amount,jdbcType=DOUBLE},


    RECEIVED = #{received,jdbcType=DOUBLE},


    RECEIVABLE = #{receivable,jdbcType=DOUBLE},


    STATE = #{state,jdbcType=INTEGER},


    DISCARD = #{discard,jdbcType=INTEGER},


    UPDATED_TIME = #{updatedTime,jdbcType=BIGINT},


    DETAILS = #{details,jdbcType=LONGVARCHAR},


where BILL_PARTNER_ID = #{billPartnerId,jdbcType=VARCHAR}
  and BILL_TYPE = #{billType,jdbcType=VARCHAR}
  and CARD_KEY = #{cardKey,jdbcType=VARCHAR}
  and DOC_ID = #{docId,jdbcType=VARCHAR}
  and EXPIRE_DATE = #{expireDate,jdbcType=BIGINT}


update nk_doc_i_bill
set AMOUNT = #{amount,jdbcType=DOUBLE},
  RECEIVED = #{received,jdbcType=DOUBLE},
  RECEIVABLE = #{receivable,jdbcType=DOUBLE},
  STATE = #{state,jdbcType=INTEGER},
  DISCARD = #{discard,jdbcType=INTEGER},
  UPDATED_TIME = #{updatedTime,jdbcType=BIGINT},
  DETAILS = #{details,jdbcType=LONGVARCHAR}
where BILL_PARTNER_ID = #{billPartnerId,jdbcType=VARCHAR}
  and BILL_TYPE = #{billType,jdbcType=VARCHAR}
  and CARD_KEY = #{cardKey,jdbcType=VARCHAR}
  and DOC_ID = #{docId,jdbcType=VARCHAR}
  and EXPIRE_DATE = #{expireDate,jdbcType=BIGINT}


